import './MapBox.css'
import mapboxgl from 'mapbox-gl'
import MapboxGeocoder from 'mapbox-gl-geocoder'
import 'mapbox-gl-geocoder/dist/mapbox-gl-geocoder.css'
import { useEffect } from 'react'
import haversineDistance from './helpers/haversine'

function MapBox () {
  const token = 'pk.eyJ1Ijoibm9yZGJlcmciLCJhIjoiY2xudm14NDFoMDB1MzJqb2JnbHFnZ2F2NSJ9.87wgkopxDP_D8B-OkI11LQ'
  mapboxgl.accessToken = token
  let map
  let markers = []
  let toBeRemovedMarkerList = []
  let placeMarker = false
  let routeType = 'walking'
  let routeDetails

  function loadMap () {
    const veszprem = { longitude: 17.909302, latitude: 47.102809 }
    const containerId = 'map'
    const zoom = 12

    map = new mapboxgl.Map({
      container: containerId,
      style: 'mapbox://styles/mapbox/streets-v12',
      center: [veszprem.longitude, veszprem.latitude],
      zoom: zoom
    })
      .addControl(new mapboxgl.NavigationControl(), 'bottom-right')
      .on('click', (event) => {
        const mouseCoordinate = event.lngLat.wrap()
        addMarker(mouseCoordinate)
      })
    //map.addControl(new mapboxgl.NavigationControl(), 'bottom-right')

    /* map.on('click', (event) => {
      const mouseCoordinate = event.lngLat.wrap()
      addMarker(mouseCoordinate)
    })
 */
    addGeocoder()
  }

  function addGeocoder () {
    const geocoder = new MapboxGeocoder({
      accessToken: mapboxgl.accessToken,
      mapboxgl: mapboxgl
    })

    geocoder.on('result', (event) => {
      const resultLongitude = event.result.center[0]
      const resultLatitude = event.result.center[1]
      const geocoderResultCoordinates = { lng: resultLongitude, lat: resultLatitude }

      addMarker(geocoderResultCoordinates)
    })
    map.addControl(geocoder)
  }

  function removeMarkers (list) {
    list.forEach(element => {
      element.remove()
    })
    markers = markers.filter(item => !list.includes(item))
  }

  function addMarker (coordinates) {
    if (placeMarker) {
      if (markers.length < 26) {
        markers.push(new mapboxgl.Marker()
          .setDraggable(true)
          .setLngLat([coordinates.lng, coordinates.lat])
          .addTo(map)
        )
      }
    } else {
      toBeRemovedMarkerList = markers.filter(marker =>
        haversineDistance(marker.getLngLat().lat, marker.getLngLat().lng, coordinates.lat, coordinates.lng) < 70)

      removeMarkers(toBeRemovedMarkerList)
    }
  }

  async function retrieveDirection () {
    const coordinateArray = markers.map((marker) => marker.getLngLat().lng + ',' + marker.getLngLat().lat)
    const coordinates = coordinateArray.join(';', ',')

    const response = await fetch(
      `https://api.mapbox.com/directions/v5/mapbox/${routeType}/${coordinates}?annotations=distance,duration&overview=full&geometries=geojson&access_token=${token}`)
    routeDetails = await response.json()
    drawRoute(await routeDetails.routes[0])
  }

  function drawRoute (data) {
    const instructions = document.getElementById('instructions')
    const legs = data.legs

    let tripInstructions = ''
    for (const step of legs) {
      const destination = step.summary.split(',').join(' => ', ',')
      tripInstructions += `<li id='instruction-item'>${destination}
                          <div>${Math.round(step.distance)} meter </div>
                          <div>${Math.floor(step.duration / 60)} min </div>
                          </li>`
    }
    instructions.innerHTML = `<p><strong>Trip distance: ${Math.round(
      data.distance
    )} meter <p><strong>Trip duration: ${Math.floor(
      data.duration / 60
    )} min </strong></p><ol>${tripInstructions}</ol>`

    const route = data.geometry.coordinates
    const geojson = {
      type: 'Feature',
      properties: {},
      geometry: {
        type: 'LineString',
        coordinates: route
      }
    }

    if (map.getSource('route')) {
      map.getSource('route').setData(geojson)
    } else {
      map.addLayer({
        id: 'route',
        type: 'line',
        source: {
          type: 'geojson',
          data: geojson
        },
        layout: {
          'line-join': 'round',
          'line-cap': 'round'
        },
        paint: {
          'line-color': '#3887be',
          'line-width': 5,
          'line-opacity': 0.75
        }
      })
    };
  }

  function changeRouteType (type) {
    routeType = type
  }

  function changeMarkerPlacerState () {
    placeMarker = !placeMarker
  }

  useEffect(() => {
    loadMap()
  }, [])

  return (
    <div className='box'>
      <header>
        <meta name="viewport" content="initial-scale=1,maximum-scale=1,user-scalable=no"/>
        <link href='https://api.mapbox.com/mapbox-gl-js/v2.14.1/mapbox-gl.css' rel='stylesheet' />
      </header>
      <div>
        <div className='settings'>
          <div>
            <label>Place marker</label>
            <input type='checkbox' onClick={() => changeMarkerPlacerState()}/>
          </div>
          <div className='planner'>Route by:</div>
          <div className='routes'>
            <label>Traffic</label>
            <input type='checkbox' onClick={ () => changeRouteType('driving-traffic')}/>
            <label>Driving</label>
            <input type='checkbox' onClick={() => changeRouteType('driving')}/>
            <label>Walking</label>
            <input type='checkbox' onClick={() => changeRouteType('walking')}/>
            <label>Cycling</label>
            <input type='checkbox' onClick={() => changeRouteType('cycling')}/>
          </div>
          <button id='plan-route-button' onClick={() => retrieveDirection()}>Plan route </button>
        </div>
        <div id="map"></div>
        <div id="instructions" ></div>
        <div>
        </div>
      </div>
    </div>
  )
}

export default MapBox
