export const drawRoute = (data, map) => {
  const instructions = document.getElementById('instructions')
  const legs = data.legs

  let tripInstructions = ''
  for (const step of legs) {
    const destination = step.summary.split(',').join(' => ', ',')
    tripInstructions += `<li id='instruction-item'> ${destination}
                        <div>${Math.round(step.distance)} meter </div>
                        <div>${Math.floor(step.duration / 60)} min </div>
                        </li>`
  }

  instructions.innerHTML = `<p><strong>Trip distance: ${Math.round(
    data.distance
  )} meter <p><strong>Trip duration: ${Math.floor(
    data.duration / 60
  )} min </strong></p><ol>${tripInstructions}</ol>`

  const route = data.geometry.coordinates
  const geojson = {
    type: 'Feature',
    properties: {},
    geometry: {
      type: 'LineString',
      coordinates: route
    }
  }

  if (map.getSource('route')) {
    map.getSource('route').setData(geojson)
  } else {
    map.addLayer({
      id: 'route',
      type: 'line',
      source: {
        type: 'geojson',
        data: geojson
      },
      layout: {
        'line-join': 'round',
        'line-cap': 'round'
      },
      paint: {
        'line-color': '#3887be',
        'line-width': 5,
        'line-opacity': 0.75
      }
    })
  };
}
