import mapboxgl from 'mapbox-gl'
import MapboxGeocoder from 'mapbox-gl-geocoder'

function createMap (addMarker) {
  const veszprem = { longitude: 17.909302, latitude: 47.102809 }
  const containerId = 'map'
  const zoomLevel = 12

  return new mapboxgl.Map({
    container: containerId,
    style: 'mapbox://styles/mapbox/streets-v12',
    center: [veszprem.longitude, veszprem.latitude],
    zoom: zoomLevel
  }).on('click', (event) => {
    const mouseCoordinate = event.lngLat.wrap()
    addMarker(mouseCoordinate)
  })
}

function addNavigationControl (map) {
  map.addControl(new mapboxgl.NavigationControl(), 'bottom-right')
}

function addGeocoder (map, addMarker) {
  const geocoder = new MapboxGeocoder({
    accessToken: mapboxgl.accessToken,
    mapboxgl: mapboxgl
  })

  geocoder.on('result', (event) => {
    const resultLongitude = event.result.center[0]
    const resultLatitude = event.result.center[1]
    const geocoderResultCoordinates = { lng: resultLongitude, lat: resultLatitude }

    addMarker(geocoderResultCoordinates)
  })
  map.addControl(geocoder)
}

function setMap (map, addMarker) {
  createMap(map, addMarker)
  addNavigationControl(map)
  addGeocoder(map, addMarker)
}

export default initializeMap
